sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (newValue === '') {
      return;
   }
	//alert(newValue);
   // when the request type changes, change the options for what needed
	var whatneeded = g_form.getValue('u_what_needed');

	// clear existing choices
	g_form.clearOptions('u_what_needed');

	// create the mapping of req type to what needed value
	var optArray = { 'hr' : 'Human Resources', 'facilities' : 'Facilities', 'legal' : 'Legal' };
	//alert(optArray);
	// get based on old value
	var optBase = optArray[newValue];
	//alert(optBase);
	if (optBase) { // add 1, 2, and Other
		//alert('adding options');
		g_form.addOption('u_what_needed', newValue + '1', optBase + ' 1');
		g_form.addOption('u_what_needed', newValue + '2', optBase + ' 2');
		g_form.addOption('u_what_needed', 'other', 'Other');
	}

	// If the form is loading and it is not a new record, set the u_what_needed value to the
	// value from the record before it was loaded
	if(isLoading && !g_form.isNewRecord()){
		g_form.setValue('u_what_needed', whatneeded);
	}
}
