sysauto_script.script:
// find overdue tasks
var rn = new GlideDateTime();
gs.info("rn: " + rn.toString());
var tomorrow = new GlideDateTime();
tomorrow.addDaysLocalTime(1);
gs.info("tom: " + tomorrow.toString());

// find all tasks before now, unclosed
var overdue = new GlideRecord('x_58872_needit_needit_task');
overdue.addQuery('due_date', '>=', rn);
overdue.addQuery('due_date', '<=', tomorrow);
overdue.addQuery('state', '!=', 3);

overdue.query();
// log out each
while (overdue.next()) {
	gs.info("NeedIt Task due soon! Record: " + overdue.number);
	gs.eventQueue('x_58872_needit.dueSoonNeedItTasks', overdue, overdue.number, gs.getUserName());
}
