sysauto_script.script:
// check the number of days before closing
var daysToClose = gs.getProperty('x_58872_needit.autoCloseOverdue');
// gs.info("autoCloseOverdue property: " + daysToClose);
if (daysToClose < 0) {
	gs.warn("autoCloseOverdue property is invalid! {0}", daysToClose);
} else {
	// find any open records older than that
	var lastDate = gs.daysAgo(daysToClose);
// 	gs.info("last date: " + lastDate);
	var overdue = new GlideRecord('x_58872_needit_needit_task');
	overdue.addQuery('due_date', '<=', lastDate);
	overdue.addQuery('state', '<', 3);
	overdue.query();

	// log each and set to closed incomplete (4);
	while (overdue.next()) {
		gs.info("Auto close NeedIt Task record: " + overdue.number);
		overdue.work_notes = "Record closed because it was too far past due date";
		overdue.state = 4;
		overdue.update();
	}
}
